#include "Header.h"



int existHelper(char** board, int x, int y, int x_max, int y_max, int indexWord, char *word) {
	// On recois une lettre a rechercher en partant d'une position donnée
	///
	/// Vers la gauche
	///
	if (x != 0 && board[y][x - 1] == word[indexWord]) {
		if (word[++indexWord] != '\0') {
			board[y][x - 1] = '0';
			if (existHelper(board, x - 1, y, x_max, y_max, indexWord, word) == -1) return -1;
			else board[y][x - 1] = word[--indexWord];
		}
		else return -1;
		
	}
	///
	/// Vers la droite
	///
	if (x+1 < x_max && board[y][x + 1] == word[indexWord]) {
		if (word[++indexWord] != '\0') {
			board[y][x + 1] = '0';
			if (existHelper(board, x + 1, y, x_max, y_max, indexWord, word) == -1) return -1;
			else board[y][x + 1] = word[--indexWord];
			
		}
		else return -1;
	}
	///
	/// Vers le haut
	///
	if (y != 0 && board[y-1][x] == word[indexWord]) {
		if (word[++indexWord] != '\0') {
			board[y-1][x] = '0';
			if (existHelper(board, x, y-1, x_max, y_max, indexWord, word) == -1) return -1;
			else board[y-1][x] = word[--indexWord];
		}
		else return -1;
		
	}
	///
	/// Vers le bas
	///
	if (y+1 < y_max && board[y+1][x] == word[indexWord]) {
		if (word[++indexWord] != '\0') {
			board[y+1][x] = '0';
			if (existHelper(board, x, y+1, x_max, y_max, indexWord, word) == -1)return -1;
			else board[y+1][x] = word[--indexWord];
		}
		else return -1;
		
	}
	return 0;
}


bool exist(char** board, int boardSize, int* boardColSize, char* word) {
	int x_max = boardColSize[0];
	int y_max = boardSize;
	char temp;

	// Find the first letter
	for (int y = 0; y < y_max; y++) {
		for (int x = 0; x < x_max; x++) {
			if (board[y][x] == word[0]) {
				temp = word[0];
				board[y][x] = '0';
				if (word[1] != '\0') {
					if (existHelper(board, x, y, x_max, y_max, 1, word) == -1)
						return true;
					else {
						board[y][x] = temp;
					}

				}
				else return true;
			}
		}
	}
	return false;

}


